const API_BASE = "https://nimbustodo.azurewebsites.net/api"; // Later, update this to your deployed Azure Function URL

export const addTask = async (task) => {
    console.log("ðŸ›°ï¸ Sending task to Azure (add):", task);

    const res = await fetch("https://nimbustodo.azurewebsites.net/api/add_task", {
      method: "POST",
      headers: { "Content-Type": "application/json" },
      body: JSON.stringify(task),
    });

    const result = await res.json();
    console.log("âœ… Azure responded to add_task:", result);
    return result;
  };

  export const deleteTask = async (taskId) => {
    console.log("ðŸ—‘ï¸ Sending delete request for task:", taskId);

    const res = await fetch(`https://nimbustodo.azurewebsites.net/api/delete_task/${taskId}`, {
      method: "DELETE",
    });

    const result = await res.text();
    console.log("âœ… Azure responded to delete_task:", result);
    return result;
  };

  export const editTask = async (taskId, updates) => {
    console.log("âœï¸ Sending update for task:", taskId, "with data:", updates);

    const res = await fetch(`https://nimbustodo.azurewebsites.net/api/edit_task/${taskId}`, {
      method: "PUT",
      headers: { "Content-Type": "application/json" },
      body: JSON.stringify(updates),
    });

    const result = await res.json();
    console.log("âœ… Azure responded to edit_task:", result);
    return result;
  };

  export const fetchUserTasks = async (userId) => {
    try {
      const res = await fetch(`${API_BASE}/get_tasks_by_user/${userId}`);
      const data = await res.json();
      console.log("ðŸ“¦ Grouped tasks for user:", data);
      return data.groups; // expects: [{ id, title, tasks: [{ id, title, priority }] }]
    } catch (error) {
      console.error("Error fetching user tasks:", error);
      return [];
    }
  };

  // ðŸ†• Create a new task group
  export const createNewTaskGroup = async (userId, title) => {
    try {
      const res = await fetch(`${API_BASE}/create_group`, {
        method: "POST",
        headers: { "Content-Type": "application/json" },
        body: JSON.stringify({ userId, title }),
      });
      const data = await res.json();
      console.log("âœ… Created group:", data);
      return await fetchUserTasks(userId);
    } catch (error) {
      console.error("Error creating group:", error);
      return [];
    }
  };

  // ðŸ†• Create a task inside a group
  export const createNewTask = async (userId, task) => {
    try {
      const payload = {
        userId,
        title: task.title,
        priority: task.priority,
      };

      if (task.groupId) {
        payload.groupId = task.groupId;
      }

      const res = await fetch(`${API_BASE}/add_grouped_task`, {
        method: "POST",
        headers: { "Content-Type": "application/json" },
