import React, { useEffect, useState } from "react";
import { useAuth } from "./context/AuthContext";
import {
  fetchUserTasks,
  createNewTaskGroup,
  createNewTask,
  deleteTask,
} from "./api/azureFunctions";
import { Trash04Icon, LayoutGrid01Icon } from "hugeicons-react";
import "./Dashboard.css";

const Dashboard = () => {
  const { user } = useAuth();
  const [taskGroups, setTaskGroups] = useState([]);
  const [newGroupTitle, setNewGroupTitle] = useState("");
  const [newTask, setNewTask] = useState({
    title: "",
    priority: "Low",
    groupId: "",
  });
  const [filter, setFilter] = useState("All");

  useEffect(() => {
    if (user) {
      fetchUserTasks(user.uid).then(setTaskGroups);
    }
  }, [user]);

  const handleCreateGroup = async () => {
    if (!newGroupTitle.trim()) return;
    const updatedGroups = await createNewTaskGroup(user.uid, newGroupTitle);
    setTaskGroups(updatedGroups);
    setNewGroupTitle("");
  };

  const handleCreateTask = async () => {
    if (!newTask.title.trim()) return;
    const updatedGroups = await createNewTask(user.uid, newTask);
    setTaskGroups(updatedGroups);
    setNewTask({ title: "", priority: "Low", groupId: "" });
  };

  const handleDeleteTask = async (taskId) => {
    await deleteTask(taskId);
    const updatedGroups = await fetchUserTasks(user.uid);
    setTaskGroups(updatedGroups);
  };

  const standaloneTasks = [
    {
      id: "1",
      title: "Create design system",
      priority: "High",
      source: "Odama Website",
    },
    {
      id: "2",
      title: "Create 3 alternative hero section",
      priority: "Medium",
      source: "Dribbble",
    },
    {
      id: "3",
      title: "Upload dribbble shot",
      priority: "Low",
      source: "Dribbble",
    },
  ];

  return (
    <div className="dashboard-wrapper">
      <aside className="sidebar">
        <div className="brand">Nimbus Todo</div>
        <div className="menu-section">
          <div className="menu-header">Projects</div>
          {taskGroups.map((group) => (
            <div key={group.id} className="menu-item">
              {group.title}
            </div>
          ))}
          <div className="new-group">
            <input
              type="text"
              value={newGroupTitle}
              onChange={(e) => setNewGroupTitle(e.target.value)}
              placeholder="New group"
            />
            <button onClick={handleCreateGroup}>+</button>
          </div>
        </div>
        <div className="user-footer">{user?.name || "User"}</div>
      </aside>

      <main className="dashboard-main">
        <header className="dashboard-header">
          <h1>Today’s Task</h1>
        </header>

        <div className="tasks-section">
          {standaloneTasks.map((task) => (
            <div className="task-card" key={task.id}>
              <div className="task-left">
                <input type="checkbox" className="task-checkbox" />
                <div className="task-info">
                  <div className="task-source">
                    <LayoutGrid01Icon size={14} />
                    {task.source}
                  </div>
                  <div className="task-title">{task.title}</div>
                </div>
              </div>
              <div className="task-actions">
                <div className={`priority-pill ${task.priority.toLowerCase()}`}>
                  <span className="priority-dot" />
                  {task.priority}
                  <span className="dropdown-arrow">▾</span>
                </div>
                <button className="trash-button">
                  <Trash04Icon size={18} />
                </button>
              </div>
            </div>
          ))}
        </div>
      </main>
    </div>
  );
};

export default Dashboard;
