import React, { useState, useEffect } from "react";
import { useAuth } from "./context/AuthContext"; // Your authentication context
import { fetchUserTasks, addTask, deleteTask, editTask } from "./azure"; // Azure backend calls
import "./Dashboard.css"; // Optional, for your styles

const Dashboard = () => {
  const { user } = useAuth(); // Logged in user info
  const [tasks, setTasks] = useState([]);
  const [loading, setLoading] = useState(true);
  const [newTaskDescription, setNewTaskDescription] = useState("");
  const [newTaskPriority, setNewTaskPriority] = useState("Low");
  const [error, setError] = useState("");

  // Fetch tasks for this user when component loads
  useEffect(() => {
    const loadTasks = async () => {
      if (user) {
        try {
          const data = await fetchUserTasks(user.localAccountId); // ⚡ Pass userId
          setTasks(data);
          setLoading(false);
        } catch (err) {
          console.error("Failed to load tasks:", err);
          setError("Failed to load tasks.");
          setLoading(false);
        }
      }
    };

    loadTasks();
  }, [user]);

  // Add a new task
  const handleAddTask = async () => {
    if (!newTaskDescription.trim()) return;

    try {
      const newTask = await addTask({
        description: newTaskDescription,
        priority: newTaskPriority,
        userId: user.localAccountId, // ⚡ Attach userId
      });

      setTasks((prev) => [...prev, newTask]);
      setNewTaskDescription("");
      setNewTaskPriority("Low");
    } catch (err) {
      console.error("Failed to add task:", err);
      setError("Failed to add task.");
    }
  };

  if (loading) {
    return <div className="loading">Loading tasks...</div>;
  }

  if (error) {
    return <div className="error">{error}</div>;
  }

  return (
    <div className="dashboard-wrapper">
      <h1>Welcome, {user?.name || user?.username || "User"}!</h1>

      {/* New Task Form */}
      <div className="new-task-form">
        <input
          type="text"
          placeholder="Enter task description"
          value={newTaskDescription}
          onChange={(e) => setNewTaskDescription(e.target.value)}
        />
        <select
          value={newTaskPriority}
          onChange={(e) => setNewTaskPriority(e.target.value)}
        >
          <option value="Low">Low</option>
          <option value="Medium">Medium</option>
          <option value="High">High</option>
        </select>
        <button onClick={handleAddTask}>Add Task</button>
      </div>

      {/* Tasks List */}
      <div className="tasks-list">
        {tasks.length === 0 ? (
          <p>No tasks yet. Add your first one!</p>
        ) : (
          tasks.map((task) => (
            <div key={task.id} className="task-card">
              <h3>{task.description}</h3>
              <p>Priority: {task.priority}</p>
              <p>Status: {task.status}</p>
            </div>
          ))
        )}
      </div>
    </div>
  );
};

export default Dashboard;
