// msal.js

import { PublicClientApplication } from "@azure/msal-browser";

const msalConfig = {
  auth: {
    clientId: "6438927f-5f93-41f7-80aa-d6516cd19114",
    authority: "https://login.microsoftonline.com/common",
    redirectUri: "http://localhost:3000", // ðŸ”¥ or your deployed frontend URL
  },
};

export const loginRequest = {
  scopes: ["User.Read"], // You can adjust the scopes if needed
};

export const msalInstance = new PublicClientApplication(msalConfig);

// Function to handle Microsoft Login
export const handleMicrosoftLogin = async () => {
  try {
    const response = await msalInstance.loginPopup(loginRequest);
    return response.account;
  } catch (error) {
    console.error("Microsoft login error:", error);
    throw error;
  }
};
